import React from 'react';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Card, Button } from 'react-bootstrap';
import Rating from '../components/Rating';
import products from '../products';

const ProductScreen = ({ match }) => {
	const product = products.find((p) => p._id === match.params.id);

	return (
		<>
			<Link className='btn btn-light my-3' to='/'>
				প্রোডাক্ট পেইজ
			</Link>
			<div className='row'>
				<div className='col-lg-5 ud-product-card'>
					<div className='summary entry-summary uc-summary ud-product-card__inner'>{product.name}</div>

					<div className='price'>{product.price}</div>
				</div>
				<div className='col-lg-7 ud-product-details d-contents d-block--lg ud-prodcut-gallery'>
					<div className='product-gallery'>
						<img src={product.image} alt='' />
					</div>
					<div className='product-description'>{product.description}</div>
					<div className='product-reviews mb-40 mt-30'>Product Reviews</div>
				</div>
			</div>
		</>
	);
};

export default ProductScreen;
